import React, { useState, Component, useEffect } from 'react';
import { withIronSession } from "next-iron-session";
import { useRouter } from 'next/router';
import {Avatar, makeStyles, Modal,
    AppBar,Toolbar,IconButton,Icon, Button, CssBaseline, TextField, FormControlLabel, Checkbox,Link ,Grid,Box, Typography} from '@material-ui/core';
import FacebookIcon from '@material-ui/icons/Facebook';
import YouTubeIcon from '@material-ui/icons/YouTube';
import MenuIcon from '@material-ui/icons/Menu';

import fire from '../../config/fire-config';

const PrivatePage = ({ user }) => {
  const [blogs, setBlogs] = useState([]);
  const [urlAtual, setUrlAtual] = useState("");
  const [canal, setCanal] = useState("");
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
useEffect(() => {
    fire.database()
      .ref('infoAtual')
      .once("value").then((snap) => {
        
        /*const blogs = snap.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));*/
        console.log('here');
        var res = (snap.val())
        setCanal(res.canal);
        setUrlAtual(res.url);
      
       // setBlogs(blogs);
      });
  }, []);

  function rand() {
    return Math.round(Math.random() * 20) - 10;
  }
  
  function getModalStyle() {
    const top = 50 + rand();
    const left = 50 + rand();
  
    return {
      top: `${top}%`,
      left: `${left}%`,
      transform: `translate(-${top}%, -${left}%)`,
    };
  }
  

  const useStyles2 = makeStyles((theme) => ({
    paper: {
      position: 'absolute',
      width: 400,
      backgroundColor: theme.palette.background.paper,
      border: '2px solid #000',
      boxShadow: theme.shadows[5],
      padding: theme.spacing(2, 4, 3),
    },
  }));
  const classes2 = useStyles2();
  const [modalStyle] = React.useState(getModalStyle);

  class AbrirModalChangeServer extends Component {
      render(){

          return(
              <div>
                <Modal
                      open={open}
                      onClose={handleClose}
                     
                    >
                     <div style={modalStyle}  className={classes2.paper}>
                            <h2 id="simple-modal-title">Text in a modal</h2>
                            <p id="simple-modal-description">
                              Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
                            </p>
                          
                          </div>
                    </Modal>
              </div>

          )

      }

  }

  class InfosAtuais extends Component {
    render(){
      if(canal == "youtube"){
        return(
          <div >
              
              <Typography variant="h6" className={classes.title}>
                  Informações atuais da NSLIVE
                </Typography>
              
               <YouTubeIcon color="secondary" style={{fontSize:"7rem",height:"55%"}}/>
               <Typography variant="h6" className={classes.title}>URL ATUAL: {urlAtual}</Typography>
               <Button onClick={handleOpen} color="inherit">Alterar</Button>

          </div>
    
    
        )

      }else{
        return(
          <div>
              <h1 >Informações atuais da NSLIVE</h1>
              <h4>{urlAtual}</h4>
              <Icon color="primary" style={{fontSize:"10rem"}}>facebook</Icon>

          </div>
        
    
        )

      }
   }

  }
  
  const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    menuButton: {
      marginRight: theme.spacing(2),
    },
    title: {
      flexGrow: 1,
    },
  }));

  const classes = useStyles();

    return(
  <div>
     <link href="https://fonts.googleapis.com/icon?family=Material+Icons"
      rel="stylesheet"></link>


    <AppBar position="static">
  <Toolbar>
    <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
      <MenuIcon />
    </IconButton>
    <Typography variant="h6" className={classes.title}>
      NSLIVE
    </Typography>
    <h6>{user.email}</h6>
    <Button color="inherit">sair</Button>
  </Toolbar>
</AppBar>
          <Grid container spacing={3}>  
              <Grid item xs={3}>
              </Grid>
              <Grid item xs={3}>
                  <InfosAtuais/>
              </Grid>
               </Grid>

          <AbrirModalChangeServer/>
  </div>);
};



export const getServerSideProps = withIronSession(

  
  async ({ req, res }) => {
    const user = req.session.get("user");
    if (!user) {
      res.setHeader("location", "/Controle/login");
      res.statusCode = 302;
      res.end();
      return { props: {} };
    }

    return {
      props: { user }
    };
  },
  {
    cookieName: "MYSITECOOKIE",
    cookieOptions: {
      secure: process.env.NODE_ENV === "production" ? true : false
    },
    password: process.env.APPLICATION_SECRET
  }
);

export default PrivatePage;
