import React from 'react';
import YouTube from 'react-youtube';


const Example = (props) => {
  const opts = {
    height: '390',
    width: '640',
    playerVars: {
      // https://developers.google.com/youtube/player_parameters
      autoplay: 0,
    },
  };
  let ref = React.createRef();
  var player;
  const callReady = (event) =>{
    player = event.target;
      console.log('acho q deu certo');
  }
  var iframe;
  function playFullscreen (){
    console.log('acho q deu certo2');
    console.log(ref);
    console.log(ref.current.div.div.iframe);

    iframe = ref.current;

    player.playVideo();

    var requestFullScreen = iframe.requestFullScreen || iframe.mozRequestFullScreen || iframe.webkitRequestFullScreen;
    if (requestFullScreen) {
      requestFullScreen.bind(iframe)();
    }

    console.log(requestFullScreen);
  }

  return (
  <div  onClick={playFullscreen} style={{width:"100%", height:"100%"}} ref={ref} >
  <YouTube videoId="YjF85_E4p70" opts={opts} onReady={callReady} />;
  </div>);
}

export default Example;
